import React from "react";

import Button from "react-bootstrap/Button";

class ProfilePage extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      u_name : localStorage.setItem('username', ""),
      u_pass : localStorage.setItem('password', "")
    };
  }



  onButtonDelete = () => {
    localStorage.setItem('username', "");
    localStorage.setItem('password', "");
  };

  onButtonEnter = () => {
    localStorage.setItem('username', "Admin");
    localStorage.setItem('password', "12345");
  };

  render() {
    return (
      <div>
        <div className="text-right">
          <Button variant="primary" onClick={this.onButtonEnter}>
            Ввести
          </Button>

          <Button variant="primary" onClick={this.onButtonDelete}>
            Удалить
          </Button>
        </div>

        <h1>Cтраница с произвольным текстом, недоступная без авторизации</h1>
        <h2>{this.state.u_name}</h2>
        <h2>{this.state.u_pass}</h2>
        
        {console.log({localStorage})}

      </div>
    );
  }
}

export default ProfilePage;
