import React from "react";

import Button from "react-bootstrap/Button";

class ProfilePage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      u_name: "",
      u_pass: "",
    };
  }

  onButtonEnter = () => {
    localStorage.setItem("username", "Admin");
    this.setState({ u_name: "Admin" });
    localStorage.setItem("password", "12345");
    this.setState({ u_pass: "12345" });
    console.log({ localStorage });
  };

  onButtonDelete = () => {
    localStorage.setItem("username", "");
    this.setState({ u_name: "" });
    localStorage.setItem("password", "");
    this.setState({ u_pass: "" });
    console.log({ localStorage });
  };

  render() {
    return (
      <div>
        <div className="text-right">
          <Button variant="primary" onClick={this.onButtonEnter}>
            Ввести
          </Button>

          <Button variant="primary" onClick={this.onButtonDelete}>
            Удалить
          </Button>
        </div>

        <h1>Cтраница с произвольным текстом, недоступная без авторизации</h1>
        <h3>{this.state.u_name}</h3>
        <h3>{this.state.u_pass}</h3>
      </div>
    );
  }
}

export default ProfilePage;
