import React from "react";
import { createStore } from "redux";
import { Form, Button } from "react-bootstrap";

class ProfilePage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
           
    };
  }



  onlogOut = () => {
    delete localStorage.username;
    delete localStorage.password;
    localStorage.login = false;

    console.log("username: " + localStorage.usernam);
    console.log("password: " + localStorage.password);
  };

  render() {
    const reducer = (state = 0, action) => {
      switch (action.type) {
        case "INC":
          return state + 1;
        case "DEC":
          return state - 1;
        default:
          return state;
      }
    };

    const store = createStore(reducer);

    store.subscribe(() => {
      console.log(store.getState());
    });

    store.dispatch({ type: "INC" });
    store.dispatch({ type: "INC" });
    store.dispatch({ type: "INC" });

    console.log(store.getState());

    let onINC = () => store.dispatch({ type: "INC" });
    let onDEC = () => store.dispatch({ type: "DEC" });
    /*
    let state = reducer(undefined, {});
    
    state = reducer(state, { type: "INC" });
    console.log(state);
    state = reducer(state, { type: "INC" });
    console.log(state);
*/
    return (
      <div>
        <h1>Cтраница с произвольным текстом, недоступная без авторизации</h1>
        <h3>{localStorage.username}</h3>
        <h3>{localStorage.password}</h3>
        <h3>{localStorage.login}</h3>

        <Form.Group>
          <div className="text-right">
            <Button variant="primary" onClick={onINC}>
              +
            </Button>
            <Button variant="primary" onClick={onDEC}>
              -
            </Button>
          </div>
        </Form.Group>
        <h3>{this.state}</h3>

        <Button variant="primary" onClick={this.onlogOut}>
          Выйти
        </Button>
      </div>
    );
  }
}

export default ProfilePage;
